Examples:
CREATE TABLE t1 (a int NOT NULL PRIMARY KEY);
BEGIN;
INSERT INTO t1 VALUES (1);
COMMIT;

CREATE TABLE t1 (a int NOT NULL PRIMARY KEY);
START TRANSACTION;
INSERT INTO t1 VALUES (1);
COMMIT;

CREATE TABLE t1 (a INT NOT NULL PRIMARY KEY);
BEGIN;
INSERT INTO t1 VALUES (1);
ROLLBACK;
SELECT * FROM t1;

CREATE TABLE t1 (a int NOT NULL PRIMARY KEY);
BEGIN;
INSERT INTO t1 VALUES (1);
SAVEPOINT sp1;
INSERT INTO t1 VALUES (2);
SAVEPOINT sp2;
RELEASE SAVEPOINT sp2;
ROLLBACK TO SAVEPOINT sp1;
COMMIT;
SELECT * FROM t1;

