Examples:
WITH demo AS (
    SELECT 'null' AS 'v'
    UNION SELECT '"foobar"'
    UNION SELECT 'true'
    UNION SELECT '5'
    UNION SELECT '1.14'
    UNION SELECT '[]'
    UNION SELECT '{}'
    UNION SELECT POW(2,63)
)
SELECT v, JSON_TYPE(v) FROM demo ORDER BY 2;
SELECT '"2025-06-14"',CAST(CAST('2025-06-14' AS date) AS json);
SELECT JSON_TYPE('"2025-06-14"'),JSON_TYPE(CAST(CAST('2025-06-14' AS date) AS json));
